package Bitmasking;

public class Right_and_Left_Sift {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//right sift operator
		int x=86;
		System.out.println(x>>2);
		int y=-11;
		System.out.println(y>>1);
		//Left sift
		int y1=11;
		System.out.println(y1<<1);
		System.out.println(y<<1);
		int z=-5;
		System.out.println(z<<1);

	}

} 
